import React, { useState } from "react";
import "./Books.css";

const Books = () => {
  const [books, setBooks] = useState([
    { title: "To Kill a Mockingbird", author: "Harper Lee", category: "Classic" },
    { title: "1984", author: "George Orwell", category: "Dystopian" },
    { title: "The Great Gatsby", author: "F. Scott Fitzgerald", category: "Novel" },
    { title: "Pride and Prejudice", author: "Jane Austen", category: "Romance" },
    { title: "Moby Dick", author: "Herman Melville", category: "Adventure" },
  ]);

  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });

  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }

    const sortedBooks = [...books].sort((a, b) => {
      if (a[key] < b[key]) return direction === "asc" ? -1 : 1;
      if (a[key] > b[key]) return direction === "asc" ? 1 : -1;
      return 0;
    });

    setBooks(sortedBooks);
    setSortConfig({ key, direction });
  };

  return (
    <div className="library-container">
      <div className="library-header">Library Books</div>

      <table className="library-table">
        <thead>
          <tr>
            <th onClick={() => handleSort("title")}>
              Title {sortConfig.key === "title" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("author")}>
              Author {sortConfig.key === "author" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("category")}>
              Category {sortConfig.key === "category" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
          </tr>
        </thead>
        <tbody>
          {books.map((book, i) => (
            <tr key={i}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.category}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Books;
