import React, { useEffect, useState } from 'react';

function Books() {
  const [books, setBooks] = useState([]);
  const [form, setForm] = useState({ title: '', authorId: '', categoryId: '', description: '' });

  useEffect(() => {
    fetch('http://localhost:3000/books')
      .then((res) => res.json())
      .then((data) => setBooks(data))
      .catch((err) => console.error('Error fetching books:', err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3000/books', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then(() => {
        setForm({ title: '', authorId: '', categoryId: '', description: '' });
        return fetch('http://localhost:3000/books').then((res) => res.json());
      })
      .then((data) => setBooks(data))
      .catch((err) => console.error('Error adding book:', err));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>ðŸ“š Bookshelf</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Author ID"
          value={form.authorId}
          onChange={(e) => setForm({ ...form, authorId: e.target.value })}
        />
        <input
          type="text"
          placeholder="Category ID"
          value={form.categoryId}
          onChange={(e) => setForm({ ...form, categoryId: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
        />
        <button type="submit">Add Book</button>
      </form>

      <h2>Book List</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            {book.title} (Author: {book.authorId}, Category: {book.categoryId})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Books;
