import React, { useState, useEffect } from "react";

export default function AuthorsCategories() {
  const [authors, setAuthors] = useState([]);
  const [categories, setCategories] = useState([]);
  const [authorName, setAuthorName] = useState("");
  const [categoryName, setCategoryName] = useState("");

  const load = async () => {
    const a = await fetch("http://localhost:3001/authors").then(r => r.json());
    const c = await fetch("http://localhost:3001/categories").then(r => r.json());
    setAuthors(a);
    setCategories(c);
  };

  const addAuthor = async () => {
    await fetch("http://localhost:3001/authors", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: authorName })
    });
    setAuthorName("");
    load();
  };

  const addCategory = async () => {
    await fetch("http://localhost:3001/categories", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: categoryName })
    });
    setCategoryName("");
    load();
  };

  useEffect(() => { load(); }, []);

  return (
    <div>
      <h2>Authors</h2>
      <input value={authorName} onChange={e => setAuthorName(e.target.value)} placeholder="Add author" />
      <button onClick={addAuthor}>Add</button>
      <ul>{authors.map(a => <li key={a.id}>{a.name}</li>)}</ul>

      <h2>Categories</h2>
      <input value={categoryName} onChange={e => setCategoryName(e.target.value)} placeholder="Add category" />
      <button onClick={addCategory}>Add</button>
      <ul>{categories.map(c => <li key={c.id}>{c.name}</li>)}</ul>
    </div>
  );
}
