import React, { useState } from "react";
import "./Books.css";

const Books = () => {
  const [books] = useState([
    { title: "To Kill a Mockingbird", author: "Harper Lee", category: "Classic" },
    { title: "1984", author: "George Orwell", category: "Dystopian" },
    { title: "The Great Gatsby", author: "F. Scott Fitzgerald", category: "Novel" },
    { title: "Pride and Prejudice", author: "Jane Austen", category: "Romance" },
    { title: "Moby Dick", author: "Herman Melville", category: "Adventure" },
    { title: "The Catcher in the Rye", author: "J.D. Salinger", category: "Fiction" },
    { title: "The Hobbit", author: "J.R.R. Tolkien", category: "Fantasy" },
    { title: "The Alchemist", author: "Paulo Coelho", category: "Philosophy" },
    { title: "Crime and Punishment", author: "Fyodor Dostoevsky", category: "Psychological" },
    { title: "Brave New World", author: "Aldous Huxley", category: "Science Fiction" },
    { title: "The Little Prince", author: "Antoine de Saint-Exupéry", category: "Children" },
    { title: "Don Quixote", author: "Miguel de Cervantes", category: "Classic" },
    { title: "The Book Thief", author: "Markus Zusak", category: "Historical" },
  ]);

  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });
  const [searchTerm, setSearchTerm] = useState("");

  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }
    setSortConfig({ key, direction });
  };

  const sortedBooks = [...books].sort((a, b) => {
    if (!sortConfig.key) return 0;
    const key = sortConfig.key;
    const direction = sortConfig.direction === "asc" ? 1 : -1;
    return a[key].localeCompare(b[key]) * direction;
  });

  const filteredBooks = sortedBooks.filter((book) =>
    Object.values(book).some((value) =>
      value.toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  return (
    <div className="library-container">
      <h1 className="library-header">Library Books</h1>

      <input
        type="text"
        className="search-bar"
        placeholder="Search by title, author, or category..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <table className="library-table">
        <thead>
          <tr>
            <th onClick={() => handleSort("title")}>
              Title {sortConfig.key === "title" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("author")}>
              Author {sortConfig.key === "author" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
            <th onClick={() => handleSort("category")}>
              Category {sortConfig.key === "category" ? (sortConfig.direction === "asc" ? "▲" : "▼") : ""}
            </th>
          </tr>
        </thead>
        <tbody>
          {filteredBooks.map((book, index) => (
            <tr key={index}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.category}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {filteredBooks.length === 0 && (
        <p className="no-results">No books found matching your search.</p>
      )}
    </div>
  );
};

export default Books;
