import React from "react";
import { Link } from "react-router-dom";

export default function PostCard({ post }) {
  return (
    <div className="post-card">
      <h3>{post.title}</h3>
      <p>{post.content.slice(0, 120)}...</p>
      <Link to={`/posts/${post.id}`}>
        <button>Read More</button>
      </Link>
    </div>
  );
}
