import React, { useState } from "react";


import logo from "./convince.jpg";
import Login from "./Login";

function Register({ onSwitchToLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    if (password !== confirmPassword) {
      setError("Passwords do not match!");
      return;
    }

    const users = JSON.parse(localStorage.getItem("users")) || [];
    const existingUser = users.find((u) => u.username === username);

    if (existingUser) {
      setError("Username already exists!");
      return;
    }

    users.push({ username, password });
    localStorage.setItem("users", JSON.stringify(users));
    setSuccess("Registration successful! Redirecting to login...");
    setTimeout(() => onSwitchToLogin(), 1500);
  };

  return (
    <div className="register-page">
      <div className="register-card">
        <img src={logo} alt="Convince Logo" className="register-logo" />
        <h1 className="register-title">Join ConVINCE Blog</h1>
        <h2 className="register-subtitle">
          A space for thinkers, dreamers, and storytellers.
        </h2>

        <form onSubmit={handleSubmit} className="register-form">
          <input
            type="text"
            placeholder="Create a Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            className="register-input"
          />

          <input
            type="password"
            placeholder="Create a Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="register-input"
          />

          <input
            type="password"
            placeholder="Confirm Password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
            className="register-input"
          />

          {error && <div className="register-error">{error}</div>}
          {success && <div className="register-success">{success}</div>}

          <button type="submit" className="register-button">
            Register
          </button>
        </form>

        <div className="register-hint">
          Already have an account?{" "}
          <span onClick={onSwitchToLogin} className="login-link">
            Login here
          </span>
        </div>
      </div>
    </div>
  );
}

export default Register;
