import "./Blog.css";

function PostPage({ post }) {
  return (
    <div className="blog-page">
      <article className="blog-full-post">
        <h1 className="post-full-title">{post.title}</h1>
        <p className="post-full-date">{post.date}</p>
        {post.image && <img src={post.image} className="post-full-image" alt={post.title} />}
        <div className="post-full-content">{/* render post.content (HTML or markdown) */}</div>
      </article>
    </div>
  );
}

export default PostPage;
