import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./pages/Login.css"

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onLogin(username, password)) {
      navigate("/");
    } else {
      setError("Invalid username or password");
    }
  };

  return (
    <div className="login-page">
      <div className="login-card">
        <h1 className="login-title">ConVINCE Blog</h1>
        <form onSubmit={handleSubmit} className="login-form">
          <input className="login-input" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
          <input type="password" className="login-input" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
          {error && <div className="login-error">{error}</div>}
          <button type="submit" className="login-button">Login</button>
        </form>
        <div className="login-hint">
          Donâ€™t have an account? <Link to="/register" className="register-link">Register here</Link>
        </div>
      </div>
    </div>
  );
}

export default Login;
