import React from "react";
import { useNavigate } from "react-router-dom";
import {
  FaTasks,
  FaSignInAlt,
  FaTachometerAlt,
  FaCode,
  FaArrowLeft,
} from "react-icons/fa";
import "./Feed.css";

export default function ExploreProjects() {
  const navigate = useNavigate();

  const projects = [
    {
      icon: <FaTasks />,
      title: "Activity 1: Task List",
      description:
        "A dynamic to-do app designed to boost productivity and help users manage their daily workflow efficiently.",
      path: "/tasklist",
    },
    {
      icon: <FaSignInAlt />,
      title: "Activity 2: Login Page",
      description:
        "A secure and responsive login system built with best practices for user authentication and validation.",
      path: "/login",
    },
    {
      icon: <FaTachometerAlt />,
      title: "Activity 3: Dashboard",
      description:
        "An interactive dashboard for tracking data, monitoring progress, and visualizing performance insights.",
      path: "/dashboard",
    },
    {
      icon: <FaCode />,
      title: "Activity 4: Script Showcase",
      description:
        "Explore automated scripts and custom functionalities that showcase smart IT problem-solving.",
      path: "/script",
    },
  ];

  return (
    <div className="explore-page">
      {/* Hero Section */}
      <section className="hero dark-hero explore-hero">
        <div className="hero-text">
          <h1>Explore Our Projects</h1>
          <p className="subtext">
            Dive into hands-on activities and real-world IT innovations built by
            the ConVINCE community â€” where learning meets collaboration.
          </p>
        </div>
        <img
          src="https://images.unsplash.com/photo-1521791136064-7986c2920216"
          alt="Innovation projects"
          className="hero-img"
        />
      </section>

      {/* Projects Grid */}
      <section className="projects-content">
        <h2 className="section-title">Featured Activities</h2>
        <div className="project-grid">
          {projects.map((project, index) => (
            <div key={index} className="project-card">
              <div className="project-icon">{project.icon}</div>
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <button
                className="btn primary"
                onClick={() => navigate(project.path)}
              >
                View Project
              </button>
            </div>
          ))}
        </div>

        <button className="btn back-btn" onClick={() => navigate("/")}>
          <FaArrowLeft /> Back to Home
        </button>
      </section>
    </div>
  );
}
