import React, { useState } from "react";
import "./CreatePost.css";

function CreatePost() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Post created: ${title}`);
    setTitle("");
    setContent("");
  };

  return (
    <div className="create-container">
      <h2>Create New Post ✍️</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Post Title" value={title} onChange={(e)=>setTitle(e.target.value)} required />
        <textarea placeholder="Write something amazing..." value={content} onChange={(e)=>setContent(e.target.value)} required />
        <button type="submit">Publish</button>
      </form>
    </div>
  );
}

export default CreatePost;
