import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

function Register({ onRegister }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    const ok = onRegister(username, password);
    if (ok) {
      setSuccess("Registration successful! Please login.");
      setError("");
      setTimeout(() => navigate("/login"), 1500);
    } else {
      setError("Username already exists");
    }
  };

  return (
    <div className="login-page">
      <div className="login-card">
        <h1 className="login-title">Register</h1>
        <form onSubmit={handleSubmit} className="login-form">
          <input className="login-input" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
          <input type="password" className="login-input" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
          {error && <div className="login-error">{error}</div>}
          {success && <div className="login-success">{success}</div>}
          <button type="submit" className="login-button">Register</button>
        </form>
        <div className="login-hint">
          Already have an account? <Link to="/login" className="register-link">Login here</Link>
        </div>
      </div>
    </div>
  );
}

export default Register;
