import React, { useState } from "react";
import "./Login.css";
import Register from "./Register";
import Home from "./Home"; // Your blog/home component

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loggedIn, setLoggedIn] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  // Handle login
  const handleLogin = (e) => {
    e.preventDefault();
    setError("");
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const user = users.find((u) => u.email === email && u.password === password);

    if (user) {
      setLoggedIn(true);
    } else {
      setError("Invalid email or password");
    }
  };

  // Handle registration passed to Register component
  const handleRegister = (email, password) => {
    const users = JSON.parse(localStorage.getItem("users")) || [];

    if (users.find((u) => u.email === email)) {
      alert("Email already registered");
      return;
    }

    users.push({ email, password });
    localStorage.setItem("users", JSON.stringify(users));
    setShowRegister(false); // Redirect to login
    alert("Registration successful! Please login.");
  };

  if (loggedIn) return <Home onLogout={() => setLoggedIn(false)} />;
  if (showRegister) return <Register onRegister={handleRegister} />;

  return (
    <div className="login-page">
      <div className="login-card">
        <h1 className="login-title">Login</h1>
        <h2 className="login-subtitle">
          Welcome back <br /> Sign in to continue.
        </h2>

        <form onSubmit={handleLogin} className="login-form">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="login-input"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="login-input"
          />

          {error && <div className="login-error">{error}</div>}

          <button type="submit" className="login-button">
            Login
          </button>
        </form>

        <p className="login-hint">
          Don't have an account?{" "}
          <span className="register-link" onClick={() => setShowRegister(true)}>
            Register here
          </span>
        </p>
      </div>
    </div>
  );
}

export default Login;
