import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Home({ currentUser, onLogout }) {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const savedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(savedPosts);
  }, []);

  return (
    <div className="feed-container">
      <div className="feed-header">
        <h1>ConVINCE Feed</h1>
        <div>
          <Link className="auth-button" to="/create-post">New Post</Link>
          <button className="auth-button logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>

      <div className="feed">
        {posts.length === 0 && <p className="empty-feed">No posts yet.</p>}
        {posts.map((post, idx) => (
          <div key={idx} className="post-card">
            <div className="post-header">
              <strong>{post.author}</strong> <span className="post-date">{post.date}</span>
            </div>
            <div className="post-content">
              <p>{post.content}</p>
              {post.image && <img src={post.image} className="post-image" alt="" />}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
