import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Feed.css";

function Home({ currentUser, onLogout }) {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const savedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(savedPosts);
  }, []);

  const handleComment = (index, commentText) => {
    if (!commentText) return;
    const updatedPosts = [...posts];
    updatedPosts[index].comments.push({
      user: currentUser.username,
      text: commentText,
      date: new Date().toLocaleString()
    });
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
  };

  const handleDelete = (index) => {
    if (!window.confirm("Are you sure you want to delete this post?")) return;
    const updatedPosts = [...posts];
    updatedPosts.splice(index, 1);
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
  };

  return (
    <div className="feed-container">
      <div className="feed-header">
        <h2>Welcome, {currentUser.username}</h2>
        <div>
          <Link to="/create" className="auth-button">New Post</Link>
          <button onClick={onLogout} className="auth-button logout-btn">Logout</button>
        </div>
      </div>

      <div className="posts-list">
        {posts.map((post, idx) => (
          <div key={idx} className="post-card">
            <p className="post-author">
              {post.author} <span className="post-date">{post.date}</span>
            </p>

            <p className="post-content">{post.content}</p>
            {post.image && <img src={post.image} alt="post" className="post-image" />}

            {/* Show Delete button only for posts authored by current user */}
            {post.author === currentUser.username && (
              <button 
                onClick={() => handleDelete(idx)} 
                className="auth-button delete-btn"
              >
                Delete
              </button>
            )}

            <div className="comments-section">
              {post.comments.map((c, i) => (
                <p key={i} className="comment">
                  <b>{c.user}</b>: {c.text} <span className="comment-timestamp">{c.date}</span>
                </p>
              ))}
              <CommentInput onComment={(text) => handleComment(idx, text)} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function CommentInput({ onComment }) {
  const [text, setText] = useState("");
  const handleSubmit = (e) => {
    e.preventDefault();
    onComment(text);
    setText("");
  };
  return (
    <form onSubmit={handleSubmit} className="comment-form">
      <input
        type="text"
        value={text}
        placeholder="Add a comment..."
        onChange={(e) => setText(e.target.value)}
        className="comment-input"
      />
      <button type="submit" className="auth-button">Comment</button>
    </form>
  );
}

export default Home;
