import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../Blog.css";

function CreatePost({ user }) {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = e => {
    e.preventDefault();
    const posts = JSON.parse(localStorage.getItem("posts")) || [];
    const newPost = {
      id: Date.now(),
      title,
      content,
      date: new Date().toLocaleDateString(),
      image,
      author: user.username,
      comments: []
    };
    posts.unshift(newPost);
    localStorage.setItem("posts", JSON.stringify(posts));
    navigate("/");
  };

  const handleImageUpload = e => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => setImage(reader.result);
    reader.readAsDataURL(file);
  };

  return (
    <div className="blog-page">
      <h2 className="blog-title">Create New Post</h2>
      <form className="post-creator" onSubmit={handleSubmit}>
        <input value={title} onChange={e => setTitle(e.target.value)} placeholder="Post title" required />
        <textarea value={content} onChange={e => setContent(e.target.value)} placeholder="Write your post..." required />
        <input type="file" onChange={handleImageUpload} />
        <button type="submit" className="auth-button">Publish</button>
      </form>
    </div>
  );
}

export default CreatePost;
