import React, { useState, useEffect } from "react";
import "./Home.css";

function Home({ onLogout }) {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState("");
  const [postImage, setPostImage] = useState(null); // store image
  const [commentText, setCommentText] = useState({});
  const [currentUser, setCurrentUser] = useState("");

  useEffect(() => {
    const storedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(storedPosts);

    const storedUser = JSON.parse(localStorage.getItem("currentUser"));
    if (storedUser) setCurrentUser(storedUser.email);
  }, []);

  const savePosts = (updatedPosts) => {
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
  };

  const handleAddPost = () => {
    if (!newPost.trim() && !postImage) return; // require text or image

    const reader = new FileReader();
    reader.onloadend = () => {
      const imageData = reader.result; // base64 string of image

      const updatedPosts = [
        {
          id: Date.now(),
          author: currentUser,
          content: newPost,
          image: postImage ? imageData : null,
          comments: [],
          timestamp: new Date().toLocaleString(),
        },
        ...posts,
      ];

      savePosts(updatedPosts);
      setNewPost("");
      setPostImage(null);
      document.getElementById("postImageInput").value = ""; // reset file input
    };

    if (postImage) {
      reader.readAsDataURL(postImage);
    } else {
      reader.onloadend(); // no image, just add post
    }
  };

  const handleAddComment = (postId) => {
    if (!commentText[postId]?.trim()) return;

    const updatedPosts = posts.map((post) => {
      if (post.id === postId) {
        const updatedComments = [
          ...post.comments,
          {
            id: Date.now(),
            author: currentUser,
            text: commentText[postId],
            timestamp: new Date().toLocaleString(),
          },
        ];
        return { ...post, comments: updatedComments };
      }
      return post;
    });

    savePosts(updatedPosts);
    setCommentText({ ...commentText, [postId]: "" });
  };

  return (
    <div className="home-container">
      <div className="home-header">
        <h1 className="auth-title">ConVINCE Blog</h1>
        <button
          className="auth-button logout-btn"
          onClick={() => {
            localStorage.removeItem("currentUser");
            onLogout();
          }}
        >
          Logout
        </button>
      </div>

      {/* Create new post */}
      <div className="post-creator">
        <textarea
          className="auth-input post-input"
          placeholder="What's on your mind?"
          value={newPost}
          onChange={(e) => setNewPost(e.target.value)}
        />
        <input
          type="file"
          accept="image/*"
          id="postImageInput"
          className="auth-input"
          onChange={(e) => setPostImage(e.target.files[0])}
        />
        <button className="auth-button" onClick={handleAddPost}>
          Post
        </button>
      </div>

      {/* Posts feed */}
      <div className="posts-container">
        {posts.length === 0 && (
          <p className="auth-hint">No posts yet. Be the first to post!</p>
        )}

        {posts.map((post) => (
          <div key={post.id} className="blog-post-card">
            <div className="post-header">
              <span className="post-author">{post.author}</span>
              <span className="post-timestamp">{post.timestamp}</span>
            </div>
            <div className="post-content">{post.content}</div>
            {post.image && (
              <img src={post.image} alt="Post" className="post-image" />
            )}

            {/* Comments */}
            <div className="comments-section">
              {post.comments.map((c) => (
                <div key={c.id} className="comment">
                  <strong>{c.author}:</strong> {c.text}{" "}
                  <span className="comment-timestamp">{c.timestamp}</span>
                </div>
              ))}
            </div>

            <div className="post-actions">
              <input
                type="text"
                placeholder="Add a comment..."
                className="auth-input comment-input"
                value={commentText[post.id] || ""}
                onChange={(e) =>
                  setCommentText({ ...commentText, [post.id]: e.target.value })
                }
              />
              <button
                className="auth-button comment-btn"
                onClick={() => handleAddComment(post.id)}
              >
                Comment
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
