import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Home({ currentUser, onLogout }) {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const savedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(savedPosts);
  }, []);

  return (
    <div className="blog-page">
      <div className="blog-hero">
        <h1 className="blog-title">ConVINCE</h1>
        <p className="blog-bio">Welcome to my personal blog âœ¨</p>
        <div>
          <Link className="auth-button" to="/create-post">Create Post</Link>
          <button className="auth-button logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>

      <div className="posts-list">
        {posts.length === 0 && <p style={{color:"#f9a825"}}>No posts yet.</p>}
        {posts.map((post, idx) => (
          <div key={idx} className="blog-post-preview">
            {post.image && <img src={post.image} className="post-preview-image" alt="" />}
            <div className="post-preview-content">
              <h2 className="post-preview-title">{post.title}</h2>
              <p className="post-preview-date">By {post.author} on {post.date}</p>
              <p className="post-preview-excerpt">{post.content.substring(0, 100)}...</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
