import React, { createContext, useContext, useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import {
  FaLightbulb,
  FaEye,
  FaArrowRight,
  FaUsers,
  FaLaptopCode,
  FaCommentDots,
  FaPlusCircle,
  FaTrash,
} from 'react-icons/fa';
import './Feed.css';

/*
  Single-file React app designed to align with "Activity 5: Blog Platform API + UI".
  - Expects a backend with JWT auth and CRUD endpoints for users, posts, comments.
  - Features: login/register, token storage, pagination, create post, comment system, delete actions.

  How to integrate:
  - Configure API_BASE in .env (e.g. REACT_APP_API_BASE=http://localhost:4000/api)
  - Backend endpoints (example contract) are listed below in the documentation section

  NOTE: This is a single-file demo. For production split into modules.
*/

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:4000/api';

/* ------------------------
   Auth context + helpers
   ------------------------ */
const AuthContext = createContext(null);

export function useAuth() {
  return useContext(AuthContext);
}

function AuthProvider({ children }) {
  const [token, setToken] = useState(() => localStorage.getItem('token'));
  const [user, setUser] = useState(() => {
    try {
      const raw = localStorage.getItem('user');
      return raw ? JSON.parse(raw) : null;
    } catch (e) { return null; }
  });

  useEffect(() => {
    if (token) localStorage.setItem('token', token); else localStorage.removeItem('token');
  }, [token]);

  useEffect(() => {
    if (user) localStorage.setItem('user', JSON.stringify(user)); else localStorage.removeItem('user');
  }, [user]);

  const login = async (email, password) => {
    const res = await fetch(`${API_BASE}/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    if (!res.ok) throw new Error('Login failed');
    const data = await res.json();
    setToken(data.token);
    setUser(data.user || null);
    return data;
  };

  const register = async (name, email, password) => {
    const res = await fetch(`${API_BASE}/auth/register`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name, email, password }),
    });
    if (!res.ok) throw new Error('Register failed');
    const data = await res.json();
    setToken(data.token);
    setUser(data.user || null);
    return data;
  };

  const logout = () => {
    setToken(null);
    setUser(null);
  };

  const authFetch = (path, opts = {}) => {
    const headers = opts.headers ? { ...opts.headers } : {};
    if (token) headers['Authorization'] = `Bearer ${token}`;
    return fetch(`${API_BASE}${path}`, { ...opts, headers });
  };

  return (
    <AuthContext.Provider value={{ token, user, login, register, logout, authFetch }}>
      {children}
    </AuthContext.Provider>
  );
}

/* ------------------------
   Login & Register UI
   ------------------------ */
function Login() {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const submit = async (e) => {
    e.preventDefault();
    try {
      await login(email, password);
      navigate('/');
    } catch (err) {
      setError(err.message || 'Login failed');
    }
  };

  return (
    <div className="auth-page">
      <form onSubmit={submit} className="auth-form">
        <h2>Login</h2>
        {error && <div className="error">{error}</div>}
        <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

function Register() {
  const { register } = useAuth();
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const submit = async (e) => {
    e.preventDefault();
    try {
      await register(name, email, password);
      navigate('/');
    } catch (err) {
      setError(err.message || 'Register failed');
    }
  };

  return (
    <div className="auth-page">
      <form onSubmit={submit} className="auth-form">
        <h2>Register</h2>
        {error && <div className="error">{error}</div>}
        <input placeholder="Full name" value={name} onChange={(e) => setName(e.target.value)} />
        <input placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

/* ------------------------
   Home (Blog feed) - adapted from user's original
   - Fetches posts with pagination
   - Creates posts via API
   - Comments via API
   - Requires auth for create/delete
*/
function Home() {
  const { user, authFetch, token, logout } = useAuth();
  const navigate = useNavigate();

  const [posts, setPosts] = useState([]);
  const [page, setPage] = useState(1);
  const [pageSize] = useState(6);
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(false);

  // local new post state
  const [newPost, setNewPost] = useState({ title: '', text: '', image: '' });
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    fetchPosts(page);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [page]);

  async function fetchPosts(pageNum = 1) {
    setLoading(true);
    try {
      // GET /posts?page=1&limit=6
      const res = await fetch(`${API_BASE}/posts?page=${pageNum}&limit=${pageSize}`);
      if (!res.ok) throw new Error('Failed to fetch posts');
      const data = await res.json();
      // Expect { posts: [...], totalPages: n }
      setPosts(data.posts || []);
      setTotalPages(data.totalPages || 1);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  // Create post
  const handleAddPost = async () => {
    if (!token) return navigate('/login');
    if (newPost.title.trim() === '' || newPost.text.trim() === '') return;
    try {
      const res = await authFetch('/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newPost),
      });
      if (!res.ok) throw new Error('Create post failed');
      const created = await res.json();
      // optimistic: prepend and refresh page when needed
      setPosts([created, ...posts]);
      setNewPost({ title: '', text: '', image: '' });
      setShowPopup(false);
    } catch (err) {
      console.error(err);
      alert(err.message || 'Error creating post');
    }
  };

  // Delete post
  const handleDeletePost = async (postId) => {
    if (!token) return navigate('/login');
    if (!window.confirm('Delete this post?')) return;
    try {
      const res = await authFetch(`/posts/${postId}`, { method: 'DELETE' });
      if (!res.ok) throw new Error('Delete failed');
      setPosts(posts.filter((p) => p._id !== postId));
    } catch (err) {
      console.error(err);
      alert(err.message || 'Could not delete post');
    }
  };

  // Add comment
  const handleAddComment = async (postId, text) => {
    if (!token) return navigate('/login');
    if (!text.trim()) return;
    try {
      const res = await authFetch(`/posts/${postId}/comments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text }),
      });
      if (!res.ok) throw new Error('Add comment failed');
      const comment = await res.json();
      setPosts(posts.map(p => p._id === postId ? { ...p, comments: [...p.comments, comment] } : p));
    } catch (err) {
      console.error(err);
    }
  };

  // Delete comment
  const handleDeleteComment = async (postId, commentId) => {
    if (!token) return navigate('/login');
    if (!window.confirm('Delete this comment?')) return;
    try {
      const res = await authFetch(`/posts/${postId}/comments/${commentId}`, { method: 'DELETE' });
      if (!res.ok) throw new Error('Delete comment failed');
      setPosts(posts.map(p => p._id === postId ? { ...p, comments: p.comments.filter(c => c._id !== commentId) } : p));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="home">
      <header className="topbar">
        <div className="brand">ConVINCE</div>
        <nav>
          {user ? (
            <>
              <span className="welcome">Hi, {user.name}</span>
              <button onClick={() => setShowPopup(true)} className="btn">New Post</button>
              <button onClick={() => { logout(); navigate('/login'); }} className="btn link">Logout</button>
            </>
          ) : (
            <>
              <button onClick={() => navigate('/login')} className="btn">Login</button>
              <button onClick={() => navigate('/register')} className="btn">Register</button>
            </>
          )}
        </nav>
      </header>

      {/* Hero */}
      <section className="hero">
        <div className="hero-text">
          <h1>Empowering Innovation Through Collaboration</h1>
          <p>
            <span className="highlight">ConVINCE</span> connects visionary{' '}
            <span className="highlight">minds</span> to transform ideas into
            real-world IT solutions.
          </p>
        </div>
        <img src="convince-logo.png" alt="ConVINCE Logo" className="hero-img" />
      </section>

      {/* Blog Section */}
      <section className="blogs">
        <div className="blogs-header">
          <h2>Community Blog Posts</h2>
          <div className="controls">
            <button className="add-post-btn" onClick={() => setShowPopup(true)}>
              <FaPlusCircle /> Add Post
            </button>
          </div>
        </div>

        {loading ? (
          <div>Loading...</div>
        ) : (
          <div className="blog-grid">
            {posts.map((post) => (
              <div key={post._id} className="blog-card">
                <div className="blog-icon"><FaUsers /></div>
                <h3>{post.title}</h3>
                <p>{post.text}</p>
                {post.image && <img src={post.image} alt={post.title} className="blog-image" />}

                <div className="card-actions">
                  <button className="read-more" onClick={() => navigate(`/posts/${post._id}`)}>
                    Read More <FaArrowRight className="arrow" />
                  </button>

                  {user && user._id === post.author?._id && (
                    <button className="delete-btn" onClick={() => handleDeletePost(post._id)}>
                      <FaTrash /> Delete Post
                    </button>
                  )}
                </div>

                <div className="comments-section">
                  <h4><FaCommentDots /> Comments</h4>
                  <ul>
                    {post.comments?.map((c) => (
                      <li key={c._id} className="comment-item">
                        <span><strong>{c.author?.name || 'Anon'}:</strong> {c.text}</span>
                        {user && (user._id === c.author?._id || user._id === post.author?._id) && (
                          <button className="delete-comment-btn" onClick={() => handleDeleteComment(post._id, c._id)}><FaTrash /></button>
                        )}
                      </li>
                    ))}
                  </ul>

                  <input type="text" placeholder="Write a comment..." onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      handleAddComment(post._id, e.target.value);
                      e.target.value = '';
                    }
                  }} />
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Pagination */}
        <div className="pagination">
          <button disabled={page <= 1} onClick={() => setPage(p => Math.max(1, p-1))}>Prev</button>
          <span>Page {page} / {totalPages}</span>
          <button disabled={page >= totalPages} onClick={() => setPage(p => Math.min(totalPages, p+1))}>Next</button>
        </div>
      </section>

      {/* Add New Post Popup */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>Add a New Post</h3>
            <input type="text" placeholder="Post Title" value={newPost.title} onChange={(e) => setNewPost({ ...newPost, title: e.target.value })} />
            <textarea placeholder="Write your post..." value={newPost.text} onChange={(e) => setNewPost({ ...newPost, text: e.target.value })} />
            <input type="text" placeholder="Image URL (optional)" value={newPost.image} onChange={(e) => setNewPost({ ...newPost, image: e.target.value })} />
            <div className="popup-actions">
              <button className="btn primary" onClick={handleAddPost}>Add Post</button>
              <button className="btn secondary" onClick={() => setShowPopup(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Mission Section */}
      <section className="mission">
        <div className="mission-item">
          <div className="mission-header">
            <FaLightbulb className="icon" />
            <h3>Our Mission</h3>
          </div>
          <p>To inspire the next generation of IT innovators through collaboration and creativity.</p>
        </div>

        <div className="mission-item">
          <div className="mission-header">
            <FaEye className="icon" />
            <h3>Join the Movement</h3>
          </div>
          <p>Become part of our movement to drive innovation and excellence in IT.</p>
        </div>
      </section>
    </div>
  );
}

/* ------------------------
   Post details page (simple)
   ------------------------ */
function PostDetails() {
  const { authFetch } = useAuth();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const id = window.location.pathname.split('/').pop();

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const res = await fetch(`${API_BASE}/posts/${id}`);
        if (!res.ok) throw new Error('Failed');
        const data = await res.json();
        if (mounted) setPost(data);
      } catch (err) {
        console.error(err);
      } finally { setLoading(false); }
    })();
    return () => { mounted = false; };
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (!post) return <div>Post not found</div>;

  return (
    <div className="post-details">
      <button onClick={() => navigate(-1)}>Back</button>
      <h2>{post.title}</h2>
      <p>{post.text}</p>
      {post.image && <img src={post.image} alt={post.title} />}
      <h3>Comments</h3>
      <ul>
        {post.comments?.map((c) => <li key={c._id}><strong>{c.author?.name}</strong>: {c.text}</li>)}
      </ul>
    </div>
  );
}

/* ------------------------
   App + Router
   ------------------------ */
export default function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/posts/:id" element={<PostDetails />} />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

/*
  Backend API contract (example):
  - POST /api/auth/register -> { token, user }
  - POST /api/auth/login -> { token, user }
  - GET /api/posts?page=1&limit=6 -> { posts: [{ _id, title, text, image, author, comments: [{_id, text, author}]}], totalPages }
  - POST /api/posts (auth) { title, text, image } -> created post
  - DELETE /api/posts/:id (auth, owner) -> 204
  - GET /api/posts/:id -> post
  - POST /api/posts/:id/comments (auth) { text } -> created comment
  - DELETE /api/posts/:id/comments/:commentId (auth, owner or post owner) -> 204

  Make sure backend returns JSON and uses Authorization: Bearer <token> header.
*/
