import React, { useState, useEffect } from "react";
import "./Feed.css";

function Home({ currentUser, onLogout }) {
  const [posts, setPosts] = useState([]);
  const [showCreate, setShowCreate] = useState(false);

  useEffect(() => {
    const savedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(savedPosts);
  }, []);

  const handleComment = (index, commentText) => {
    if (!commentText) return;
    const updatedPosts = [...posts];
    updatedPosts[index].comments.push({
      user: currentUser.username,
      text: commentText,
      date: new Date().toLocaleString(),
    });
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
  };

  const handleDelete = (index) => {
    const updatedPosts = [...posts];
    updatedPosts.splice(index, 1);
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
  };

  const handleCreatePost = (content, image) => {
    const newPost = {
      author: currentUser.username,
      content,
      image,
      date: new Date().toLocaleString(),
      comments: [],
    };
    const updatedPosts = [newPost, ...posts];
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
    setShowCreate(false);
  };

  return (
    <div className="feed-container">
      <div className="feed-header">
        <h2>Welcome, {currentUser.username}</h2>
        <div>
          <button onClick={() => setShowCreate(true)} className="auth-button">
            New Post
          </button>
          <button onClick={onLogout} className="auth-button logout-btn">
            Logout
          </button>
        </div>
      </div>

      {showCreate && (
        <CreatePostModal
          onClose={() => setShowCreate(false)}
          onCreate={handleCreatePost}
        />
      )}

      <div className="posts-list">
        {posts.map((post, idx) => (
          <div key={idx} className="post-card">
            <p className="post-author">
              {post.author} <span className="post-date">{post.date}</span>
            </p>
            <p className="post-content">{post.content}</p>
            {post.image && (
              <img src={post.image} alt="post" className="post-image" />
            )}
            <button
  onClick={() => handleDelete(idx)}
  className="auth-button delete-btn"
>
  Delete
</button>
            )}
            <div className="comments-section">
              {post.comments.map((c, i) => (
                <p key={i} className="comment">
                  <b>{c.user}</b>: {c.text}{" "}
                  <span className="comment-timestamp">{c.date}</span>
                </p>
              ))}
              <CommentInput onComment={(text) => handleComment(idx, text)} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function CommentInput({ onComment }) {
  const [text, setText] = useState("");
  const handleSubmit = (e) => {
    e.preventDefault();
    onComment(text);
    setText("");
  };
  return (
    <form onSubmit={handleSubmit} className="comment-form">
      <input
        type="text"
        value={text}
        placeholder="Add a comment..."
        onChange={(e) => setText(e.target.value)}
        className="comment-input"
      />
      <button type="submit" className="auth-button">
        Comment
      </button>
    </form>
  );
}

function CreatePostModal({ onClose, onCreate }) {
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    let imageUrl = image ? URL.createObjectURL(image) : null;
    onCreate(content, imageUrl);
    setContent("");
    setImage(null);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>Create New Post</h3>
        <form onSubmit={handleSubmit} className="post-creator">
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="What's on your mind?"
            required
          />
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
          <div className="modal-buttons">
            <button type="submit" className="auth-button">
              Post
            </button>
            <button type="button" onClick={onClose} className="auth-button">
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Home;
