import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Home from "./pages/Home";
import CreatePost from "./pages/CreatePost";
import "./pages/Blog.css";

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  // persist login on refresh
  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("currentUser"));
    if (loggedInUser) setCurrentUser(loggedInUser);
  }, []);

  const handleLogin = (username, password) => {
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const user = users.find(u => u.username === username && u.password === password);
    if (user) {
      setCurrentUser(user);
      localStorage.setItem("currentUser", JSON.stringify(user));
      return true;
    }
    return false;
  };

  const handleLogout = () => {
    setCurrentUser(null);
    localStorage.removeItem("currentUser");
  };

  const handleRegister = (username, password) => {
    const users = JSON.parse(localStorage.getItem("users")) || [];
    if (users.find(u => u.username === username)) {
      return false;
    }
    const newUser = { username, password };
    users.push(newUser);
    localStorage.setItem("users", JSON.stringify(users));
    return true;
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={!currentUser ? <Login onLogin={handleLogin} /> : <Navigate to="/" />} />
        <Route path="/register" element={!currentUser ? <Register onRegister={handleRegister} /> : <Navigate to="/" />} />
        <Route path="/" element={currentUser ? <Home currentUser={currentUser} onLogout={handleLogout} /> : <Navigate to="/login" />} />
        <Route path="/create-post" element={currentUser ? <CreatePost currentUser={currentUser} /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
