import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Home({ currentUser, onLogout }) {
  const [posts, setPosts] = useState([]);
  const [commentText, setCommentText] = useState({}); // store comments per post

  useEffect(() => {
    const savedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(savedPosts);
  }, []);

  const handleComment = (idx) => {
    const text = commentText[idx];
    if (!text) return;
    const updatedPosts = [...posts];
    updatedPosts[idx].comments = updatedPosts[idx].comments || [];
    updatedPosts[idx].comments.push({
      author: currentUser.username,
      text,
      date: new Date().toLocaleString()
    });
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
    setCommentText(prev => ({ ...prev, [idx]: "" }));
  };

  return (
    <div className="feed-container">
      <div className="feed-header">
        <h1>ConVINCE Feed</h1>
        <div>
          <Link className="auth-button" to="/create-post">New Post</Link>
          <button className="auth-button logout-btn" onClick={onLogout}>Logout</button>
        </div>
      </div>

      <div className="feed">
        {posts.length === 0 && <p className="empty-feed">No posts yet.</p>}
        {posts.map((post, idx) => (
          <div key={idx} className="post-card">
            <div className="post-header">
              <strong>{post.author}</strong> <span className="post-date">{post.date}</span>
            </div>
            <div className="post-content">
              <p>{post.content}</p>
              {post.image && <img src={post.image} className="post-image" alt="" />}
            </div>

            {/* Comments Section */}
            <div className="comments-section">
              {post.comments && post.comments.map((c, i) => (
                <div key={i} className="comment">
                  <strong>{c.author}</strong>: {c.text} <span className="comment-timestamp">{c.date}</span>
                </div>
              ))}
              <div className="comment-input-wrapper">
                <input
                  className="comment-input"
                  type="text"
                  placeholder="Write a comment..."
                  value={commentText[idx] || ""}
                  onChange={(e) => setCommentText(prev => ({ ...prev, [idx]: e.target.value }))}
                />
                <button className="auth-button" onClick={() => handleComment(idx)}>Comment</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
