import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./Blog.css";

function PostDetails({ user }) {
  const { id } = useParams();
  const [posts, setPosts] = useState(JSON.parse(localStorage.getItem("posts")) || []);
  const post = posts.find(p => p.id === parseInt(id));
  const [commentText, setCommentText] = useState("");

  const addComment = () => {
    if (!commentText) return;
    const newPosts = posts.map(p => {
      if (p.id === post.id) {
        p.comments.push({ user: user.username, text: commentText, date: new Date().toLocaleString() });
      }
      return p;
    });
    setPosts(newPosts);
    localStorage.setItem("posts", JSON.stringify(newPosts));
    setCommentText("");
  };

  if (!post) return <p>Post not found</p>;

  return (
    <div className="blog-page">
      <div className="blog-full-post">
        <h2 className="post-full-title">{post.title}</h2>
        <p className="post-full-date">{post.date} by {post.author}</p>
        {post.image && <img src={post.image} className="post-full-image" alt={post.title} />}
        <p className="post-full-content">{post.content}</p>

        <div className="comments-section">
          <h3>Comments</h3>
          {post.comments.map((c, i) => (
            <div key={i} className="comment">
              {c.user}: {c.text} <span className="comment-timestamp">({c.date})</span>
            </div>
          ))}
          <textarea
            className="comment-input"
            value={commentText}
            onChange={e => setCommentText(e.target.value)}
            placeholder="Write a comment..."
          />
          <button className="auth-button" onClick={addComment}>Add Comment</button>
        </div>
      </div>
    </div>
  );
}

export default PostDetails;
