import React from "react";
import "./Feed.css";

const authors = [
  {
    name: "Cristel Nicole Vergara",
    title: "The Art of Coding with Style",
    desc: "Explore the balance between creativity and logic in front-end design.",
    image: "https://source.unsplash.com/600x400/?coding,computer",
  },
  {
    name: "Kate Justine Pades",
    title: "Building User-Friendly Interfaces",
    desc: "Discover how great UI/UX design enhances user satisfaction and engagement.",
    image: "https://source.unsplash.com/600x400/?ui,ux,design",
  },
  {
    name: "Cherly Vic Bakilid",
    title: "Mastering React Components",
    desc: "A beginner’s guide to reusable, modular, and clean React components.",
    image: "https://source.unsplash.com/600x400/?react,javascript,frontend",
  },
  {
    name: "Shaun Russelle Obsenare",
    title: "Backend Magic with Node.js",
    desc: "Learn how backend logic powers your favorite apps using Node.js.",
    image: "https://source.unsplash.com/600x400/?backend,nodejs,server",
  },
  {
    name: "Jhon Mark Morales",
    title: "Full-Stack Powerhouse",
    desc: "Connecting frontend and backend seamlessly for smooth app performance.",
    image: "https://source.unsplash.com/600x400/?fullstack,developer,code",
  },
];

function Home() {
  return (
    <div className="blog-container">
      {/* HEADER */}
      <header className="blog-header">
        <h1>ConVINCE Tech Blog</h1>
      </header>

      {/* MAIN CONTENT */}
      <main className="blog-main">
        <h2 className="section-title">Featured Articles</h2>
        <div className="blog-grid">
          {authors.map((author, idx) => (
            <div key={idx} className="blog-card">
              <img src={author.image} alt={author.name} className="blog-image" />
              <div className="blog-info">
                <h3>{author.title}</h3>
                <p>{author.desc}</p>
                <p className="blog-author">By {author.name}</p>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* FOOTER */}
      <footer className="blog-footer">
        <p>© 2025 ConVINCE Tech Blog — All Rights Reserved</p>
      </footer>
    </div>
  );
}

export default Home;
