import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Blog.css";

function Home({ user, onLogout }) {
  if (!user) {
    return <div>Please log in</div>;
  }  const [posts, setPosts] = useState(JSON.parse(localStorage.getItem("posts")) || []);

  useEffect(() => {
    localStorage.setItem("posts", JSON.stringify(posts));
  }, [posts]);

  return (
    <div className="blog-page">
      <header className="blog-hero">
        <h1 className="blog-title">{user.username}'s Blog</h1>
        <p className="blog-bio">Share your thoughts and ideas.</p>
        <button className="auth-button logout-btn" onClick={onLogout}>Logout</button>
        <Link to="/create" className="auth-button">New Post</Link>
      </header>

      <main className="posts-list">
        {posts.length === 0 && <p>No posts yet.</p>}
        {posts.map(post => (
          <article key={post.id} className="blog-post-preview">
            {post.image && <img src={post.image} alt={post.title} className="post-preview-image" />}
            <div className="post-preview-content">
              <h2 className="post-preview-title">{post.title}</h2>
              <p className="post-preview-date">{post.date}</p>
              <p className="post-preview-excerpt">{post.content.substring(0, 100)}...</p>
              <Link to={`/post/${post.id}`} className="read-more-btn">Read more</Link>
            </div>
          </article>
        ))}
      </main>
    </div>
  );
}

export default Home;
