import React from "react";
import { useParams, Link } from "react-router-dom";
import { blogPosts } from "./BlogData";
import { FaFacebookF, FaTwitter, FaLinkedinIn } from "react-icons/fa";
import "./Feed.css";

export default function BlogPost() {
  const { id } = useParams();
  const post = blogPosts.find((p) => p.id === id);

  if (!post) {
    return (
      <div className="blog-not-found">
        <h2>Blog Post Not Found</h2>
        <Link to="/" className="btn primary">
          Back to Home
        </Link>
      </div>
    );
  }

  return (
    <div className="blog-post-page">
      {/* HERO */}
      <div className="blog-post-hero">
        <img src={post.image} alt={post.title} />
        <div className="overlay">
          <h1>{post.title}</h1>
          <p>{post.subtitle}</p>
        </div>
      </div>

      {/* MAIN CONTENT */}
      <div className="blog-container">
        <div className="blog-content">
          {post.sections.map((sec, index) => (
            <div key={index} className="blog-section">
              <h3>{sec.heading}</h3>
              <p>{sec.text}</p>
            </div>
          ))}
          <blockquote>{post.quote}</blockquote>

          <div className="comments">
            <h4>Comments</h4>
            <p>Be the first to share your thoughts!</p>
          </div>
        </div>

        {/* SIDEBAR */}
        <aside className="blog-sidebar">
          <div className="sidebar-section">
            <h4>About the Blog</h4>
            <p>
              Explore insights and stories from innovators shaping the future of IT collaboration.
            </p>
          </div>

          <div className="sidebar-section">
            <h4>Popular Posts</h4>
            <ul>
              {post.popular.map((p, i) => (
                <li key={i}>{p}</li>
              ))}
            </ul>
          </div>

          <div className="sidebar-section">
            <h4>Follow Us</h4>
            <div className="social-links">
              <FaFacebookF />
              <FaTwitter />
              <FaLinkedinIn />
            </div>
          </div>

          <div className="sidebar-section">
            <h4>Subscribe</h4>
            <input type="email" placeholder="Enter your email" />
            <button className="btn primary">Subscribe</button>
          </div>
        </aside>
      </div>

      {/* FOOTER */}
      <footer className="blog-footer">
        © 2025 ConVINCE | Cevie State University – Empowering Innovation Team
      </footer>
    </div>
  );
}
