import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Feed.css";

function CreatePost({ currentUser }) {
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);
  const navigate = useNavigate();

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => setImage(reader.result);
    reader.readAsDataURL(file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!content && !image) return;

    const newPost = { author: currentUser.username, content, image, date: new Date().toLocaleString(), comments: [] };
    const existingPosts = JSON.parse(localStorage.getItem("posts")) || [];
    const updatedPosts = [newPost, ...existingPosts];
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
    navigate("/");
  };

  return (
    <div className="feed-container">
      <h2>Create a New Post</h2>
      <form className="post-creator" onSubmit={handleSubmit}>
        <textarea
          className="comment-input"
          placeholder="What's on your mind?"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        <button type="submit" className="auth-button">Post</button>
      </form>
    </div>
  );
}

export default CreatePost;
