import React, { useState } from "react";
import "./Blog.css";

function Home() {
  // Sample posts
  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "My First Personal Blog Post",
      date: "Nov 5, 2025",
      excerpt: "Welcome to my personal blog! Here I share stories, ideas, and thoughts.",
      image: "https://via.placeholder.com/800x300",
      content: "This is the full content of my first blog post..."
    },
    {
      id: 2,
      title: "Another Blog Entry",
      date: "Nov 4, 2025",
      excerpt: "Sharing my experience with React and building personal projects.",
      image: "https://via.placeholder.com/800x300",
      content: "Full content goes here..."
    }
  ]);

  return (
    <div className="blog-page">
      <header className="blog-hero">
        <img src="https://via.placeholder.com/900x300" className="hero-banner" alt="Vincent Dallego" />
        <h1 className="blog-title">Vincent Dallego</h1>
        <p className="blog-bio">Sharing ideas, stories & insights. Welcome to my personal blog.</p>
      </header>

      <main className="posts-list">
        {posts.map((post) => (
          <article key={post.id} className="blog-post-preview">
            {post.image && <img src={post.image} className="post-preview-image" alt={post.title} />}
            <div className="post-preview-content">
              <h2 className="post-preview-title">{post.title}</h2>
              <p className="post-preview-date">{post.date}</p>
              <p className="post-preview-excerpt">{post.excerpt}</p>
              <button className="read-more-btn">Read more</button>
            </div>
          </article>
        ))}
      </main>
    </div>
  );
}

export default Home;
