import React, { useState, useEffect } from "react";
import "./Feed.css";

function Home({ currentUser, onLogout }) {
  const [posts, setPosts] = useState([]);
  const [showCreate, setShowCreate] = useState(false);
  const [newContent, setNewContent] = useState("");
  const [newImage, setNewImage] = useState(null);

  useEffect(() => {
    const savedPosts = JSON.parse(localStorage.getItem("posts")) || [];
    setPosts(savedPosts);
  }, []);

  const savePosts = (updatedPosts) => {
    setPosts(updatedPosts);
    localStorage.setItem("posts", JSON.stringify(updatedPosts));
  };

  const handleCreatePost = (e) => {
    e.preventDefault();
    if (!newContent) return;
    const post = {
      author: currentUser.username,
      content: newContent,
      image: newImage ? URL.createObjectURL(newImage) : null,
      date: new Date().toLocaleString(),
      comments: [],
    };
    const updatedPosts = [post, ...posts];
    savePosts(updatedPosts);
    setNewContent("");
    setNewImage(null);
    setShowCreate(false);
  };

  const handleDeletePost = (index) => {
    const updatedPosts = [...posts];
    updatedPosts.splice(index, 1);
    savePosts(updatedPosts);
  };

  const handleComment = (index, commentText) => {
    if (!commentText) return;
    const updatedPosts = [...posts];
    updatedPosts[index].comments.push({
      user: currentUser.username,
      text: commentText,
      date: new Date().toLocaleString(),
    });
    savePosts(updatedPosts);
  };

  return (
    <div className="feed-container">
      {/* HEADER */}
      <div className="feed-header">
        <h2 className="feed-title">Welcome, {currentUser.username}</h2>
        <div className="feed-actions">
          <button onClick={() => setShowCreate(true)} className="auth-button">
            Create Post
          </button>
          <button onClick={onLogout} className="auth-button logout-btn">
            Logout
          </button>
        </div>
      </div>

      {/* CREATE POST MODAL */}
      {showCreate && (
        <div className="modal-overlay">
          <div className="modal-card">
            <h3>Create Post</h3>
            <form onSubmit={handleCreatePost} className="create-post-form">
              <textarea
                placeholder="What's on your mind?"
                value={newContent}
                onChange={(e) => setNewContent(e.target.value)}
                required
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setNewImage(e.target.files[0])}
              />
              <div className="modal-actions">
                <button type="submit" className="auth-button">
                  Post
                </button>
                <button
                  type="button"
                  onClick={() => setShowCreate(false)}
                  className="auth-button"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* POSTS LIST */}
      <div className="posts-list">
        {posts.map((post, idx) => (
          <div key={idx} className="post-card">
            <div className="post-header">
              <p className="post-author">{post.author}</p>
              <p className="post-date">{post.date}</p>
              {currentUser.username === post.author && (
                <button
                  onClick={() => handleDeletePost(idx)}
                  className="auth-button delete-btn"
                >
                  Delete
                </button>
              )}
            </div>
            <p className="post-content">{post.content}</p>
            {post.image && (
              <img src={post.image} alt="post" className="post-image" />
            )}

            <div className="comments-section">
              {post.comments.map((c, i) => (
                <p key={i} className="comment">
                  <b>{c.user}</b>: {c.text}{" "}
                  <span className="comment-timestamp">{c.date}</span>
                </p>
              ))}
              <CommentInput onComment={(text) => handleComment(idx, text)} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function CommentInput({ onComment }) {
  const [text, setText] = useState("");
  const handleSubmit = (e) => {
    e.preventDefault();
    onComment(text);
    setText("");
  };
  return (
    <form onSubmit={handleSubmit} className="comment-form">
      <input
        type="text"
        value={text}
        placeholder="Add a comment..."
        onChange={(e) => setText(e.target.value)}
        className="comment-input"
      />
      <button type="submit" className="auth-button">
        Comment
      </button>
    </form>
  );
}

export default Home;
