import React from "react";
import { useNavigate } from "react-router-dom";
import { FaTasks, FaSignInAlt, FaTachometerAlt, FaCode, FaArrowLeft } from "react-icons/fa";
import "./Feed.css";

export default function ExploreProjects() {
  const navigate = useNavigate();

  const projects = [
    {
      icon: <FaTasks />,
      title: "Activity 1: Task List",
      description: "A simple task management app to organize daily activities.",
      path: "/tasklist",
    },
    {
      icon: <FaSignInAlt />,
      title: "Activity 2: Login Page",
      description: "User authentication system with secure login and validation.",
      path: "/login",
    },
    {
      icon: <FaTachometerAlt />,
      title: "Activity 3: Dashboard",
      description: "An interactive dashboard showcasing analytics and controls.",
      path: "/dashboard",
    },
    {
      icon: <FaCode />,
      title: "Activity 4: Script Showcase",
      description: "Interactive scripting and automation tools demonstration.",
      path: "/script",
    },
  ];

  return (
    <div className="explore-page">
      <h1>Explore ConVINCE Projects</h1>
      <p>Discover our ongoing activities and hands-on IT projects.</p>

      <div className="project-grid">
        {projects.map((project, index) => (
          <div key={index} className="project-card">
            <div className="project-icon">{project.icon}</div>
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            <button className="btn primary" onClick={() => navigate(project.path)}>
              View Project
            </button>
          </div>
        ))}
      </div>

      <button className="btn back-btn" onClick={() => navigate("/")}>
        <FaArrowLeft /> Back to Home
      </button>
    </div>
  );
}
